mapper.update:
update account set
   account_name = #{accountName},
   balance = #{balance}
where
    account_id = #{accountId}

mapper.select:
select ac.account_id as accountId, ac.account_name as accountName, ac.balance as balance from account ac left join account_user_relation rel on ac.account_id = rel.account_id
rel.user_id = #{userId}
and account_name like concat('%', #{accountName}, '%')
